INSERT INTO single_excercises(excercise_id,excercise_name,excercise_duration,excercise_description,kcal_per_100) VALUES(1,'przysiady','00:00:04','Stajemy w rozkroku i robimy przysiad, plecy trzymamy w pozycji prostej i uważamy, żeby kolana nie wyprzedzały lini stóp, napinamy mięsnie pośladków i brzucha',32),(2,'przysiady z wyskokiem','00:00:05','Stajemy w rozkroku i robimy przysiad, plecy utrzymamy w pozycji prostej i uważamy, żeby kolana nie wyprzedzały linii stóp, napinamy mięsnie pośladków i brzucha. Przy wyjściu do góry dodatkowo robimy wspięcie na palce',40),(3,'przysiady ze wspięciem na palce','00:00:04','Stajemy w rozkroku i robimy przysiad, plecy trzymamy w pozycji prostej i uważamy, żeby kolana nuie wyprzedzały lini stóp, napinamy mięsnie pośladków i brzucha. Przy wyjściu do góry robimy wyskok i lądujemy w przysiadzie',40),(4,'krzesełko, czas: 0,5 min','00:00:30','Opieramy plecy o ścianę, stopy rozstawione na szerokość bioder, kolana zgięte do 90 stopni, ręce wyciągnięte przed siebie. Utrzymujemy pozycję napinając mięśnie pośladkowe i brzucha',100),(5,'wykrok do przodu','00:00:05','Pozycja wyjściowa: Stanie w szerokim rozkroku, jedna noga zgięta w kolanie ustawiona do przodu; tułów wyprostowany, lekko pochylony do przodu. Kucamy raz na jedno kolano, raz na drugie',30),(6,'pompki','00:00:02','Ustawiamy się w pozycji deski wysokiej, czyli w podporze przodem, ręce są rozstawione na szerokość barków, a dłonie znajdują się dokładnie pod nimi. Uginamy ręce w łokciach, utrzymując napięte całe ciało. Następnie prostujemy ręce. Przy tym ćwiczeniu ważne jest, aby dłonie trzymać ułożone w kierunku głowy, a nie na skos',30),(7,'pompki na pięściach','00:00:02','Ustawiamy się w pozycji deski wysokiej, czyli w podporze przodem, ręce są rozstawione na szerokość barków, a dłonie zaciśnięte w pięści znajdują się dokładnie pod nimi. Uginamy ręce w łokciach utrzymując napięte całe ciało. Następnie prostujemy ręce. Przy tym ćwiczeniu ważne jest, aby dłonie trzymać ułożone w kierunku głowy, a nie na skos',60),(8,'deska wysoka, czas: 1 min','00:01:00','Ustawiamy się w podporze przodem, ręce wyprostowane rozstawione na szerokość barków tworzą z ciałem kąt 90 stopni, plecy wyprostowane, napięte mięśnie pleców i brzucha. Utrzymujemy ciało w tej pozycji określony czas',600),(9,'deska na przedramionach, czas: 1 min','00:01:00','Ustawiamy się w podporze przodem na przedramionach. Ręce rozstawione na szerokość barków tworzą z ciałem kąt 90 stopni, plecy wyprostowane, napięte mięśnie pleców i brzucha. Utrzymujemy ciało w tej pozycji określony czas',600),(10,'skręty tułowia','00:00:01','Ćwiczenie rozpocznij stojąc w rozkroku. Nogi na szerokość ramion. Unieś ręce do boku równolegle do podłoża. Wykonaj skręt tułowia w prawo i w lewo.',25),(11,'nożyce pionowe','00:00:02','Kładziemy się na plecach, nogi wyprostowane, ręce ukladamy wzdłóż ciała lub pod pośladkami. Unosimy głowę, spinamy brzuch, a nstępnie unosimy nogi kilka centymetrów nad ziemię, krzyżujemy je naprzeminnie w poziommie, utrzymując spięty brzuch i odcinek lędźwiowy kręgosłupa \"przeklejony\" do podłoża',50),(12,'nożyce poziome','00:00:02','Kładziemy się na plecach, nogi wyprostowane, ręce ukladamy wzdłóż ciała lub pod pośladkami. Unosimy głowę, spinamy brzuch, a nstępnie unosimy nogi kilka centymetrów nad ziemię, krzyżujemy je naprzeminnie w pionie utrzymując spięty brzuch i odcinek lędźwiowy kręgosłupa \"przeklejony\" do podłoża',50),(13,'brzuszki','00:00:01','Pozycja wyjściowa: kładziemy się na podłodze, nogi ugięte w kolanach, ręce zakładamy za głowę, patrzymy przed siebie. Unosimy głowę i barki kilka centymetrów nad ziemię, odcinek lędźwiowy kręgosłupa \"przyklejony\" do podłogi, opuszczamy głowę ',30),(14,'brzuszki ze skrętem','00:00:01','Pozycja wyjściowa: kładziemy się na podłodze, nogi ugięte w kolanach, ręce zakładamy za głowę, patrzymy przed siebie. Unosimy głowę i barki kilka centymetrów nad ziemię, odcinek lędźwiowy kręgosłupa \"przyklejony\" do podłogi. Podnosimy jedną nowę i dotykamy ją przeciwległym łokciem. Opuszczamy głowę i ręce. To samo robimy z drugą nogą i ręką ',30),(15,'brzuszki z unoszeniem nóg','00:00:01','Pozycja wyjściowa: kładziemy się na podłodze, nogi wyprostowane, ręce zakładamy za głowę, patrzymy przed siebie. Unosimy głowę, barki i nogi kilka centymetrów nad ziemię, odcinek lędźwiowy kręgosłupa \"przyklejony\" do podłogi, opuszczamy głowę, nogi utrzymujemy w powietrzu ',40),(16,'skłony skośne w siadzie rozkrocznym','00:00:05','Pozycja wyjściowa: Siad rozkroczny , ręce w górze. Skłon do prawej nogi, do środka, do lewej nogi, powrót do pozycji wyjściowej',15),(17,'skłony w przód','00:00:05','Pozycja wyjściowa: Siad z nogami równolegle do siebie, złączonymi w stopach; stopy pionowo do góry. Pochylamy tułów w przód, ramiona wyprostowujemy w kierunku stóp (wytrzymujemy 5-10 sekund) , powrót do pozycji wyjściowej',15),(18,'ukłon japoński, czas: 0,5 min','00:00:30','Siadamy na piętach. Stopy są złączone, a kolana szeroko. Przechodzimy dłońmi do przodu. Wyciągamy wyprostowane ramiona w górę i odłóż czoło na macie. Zatrzymujemy się w tej pozycji głęboko oddychając',40),(19,'przyciąganie nogi do pośladka','00:00:10','Pozycja wyjściowa: Stanie na jednej nodze (najlepiej z podparciem), druga noga ugięta w kolanie. Chwytamy ręką za stopę w kostce i przyciągamy ugiętą nogę maksymalnie do góry zmieniamy nogę (lewa noga - lewa ręka, prawa noga - prawa ręka)',20),(20,'skrętoskłony','00:00:01','Stajemy w szerokim rozkroku, pochylamy się trzymając wyprostowane plecy i ręce wyciągnięte w bok. Sięgamy naprzemiennie do jednej i drugiej stopy, patrząc za ręką idącą ku górze',15),(21,'motyl, czas: 1 min','00:01:00','Pozycja wyjściowa: Nogi w ?motylka\" (stopa dotyka stopy, nogi ugięte w stawach kolanowych), dłonie na wewnętrznej stronie ud. dłońmi dociskamy kolana do podłoża i wytrzymujemy 5-10 sekund',20),(22,'pajacyki','00:00:01','Podskakujemy z rostawieniem nóg w rozkrok, razem z nogami robimy wymach oburącz ku górze z klaśniecięm nad głową',20),(23,'burpeesy','00:00:03','Wyskakujemy z uniesieniem rąk do góry, a następnie kucamy i wyrzucamy oby dwie nogi w tył do pozycji wysokiej deski, wracamy nogami i ponowny wyskok w górę. Aby utrudnić ćwiczenie, robimy pompkę gdy znajdziemy się w pozycji deski.',50),(24,'wymachy ramionami w przód','00:00:01','Stajemy w lekkim rozkroku i wykonujemy wymachy wyprostowanymi rękami w przód, oburącz lub naprzeminnie. Plecy trzymamy prosto, brzuch napięty',15),(25,'wymachy ramionami w tył','00:00:01','Stajemy w lekkim rozkroku i wykonujemy wymachy wyprostowanymi rękami w tył, oburącz lub naprzeminnie. Plecy trzymamy prosto, brzuch napięty',15);
